        return contacts[id]
    }

    @PyMethod
    func get_all_contacts() -&gt; [String: Person] {
        return contacts
    }

    @PyMethod
    func remove_contact(id: String) -&gt; Bool {
        if contacts[id] != nil {
            contacts.removeValue(forKey: id)
            return true
        }
        return false
    }

    @PyMethod
    func contact_count() -&gt; Int {
        return contacts.count
    }

    @PyMethod
    func get_names() -&gt; [String] {
        return contacts.values.map { $0.name }
    }
